contracts/ingest/IngestImpl.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/**
 * @title Ingest Implementation Contract
 * @author Origin Protocol Inc
 */

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./IngestRegistry.sol";

contract IngestImpl {
    using SafeERC20 for IERC20;

    // Registry address replaced at deploy time
    IngestRegistry constant registry =
        IngestRegistry(0xFFbebEbeBEbeBeBEBeBebeBEbeBebebEAAaAaAAA);

    // Special case address for ETH.
    address constant ETHEREUM =
        address(0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE);

    function collect(address asset, uint256 amount) external {
        require(
            msg.sender == registry.master(),
            "Endpoint: Caller is not the master"
        );
        address payable pool = payable(registry.pool());
        if (asset == ETHEREUM) {
            payable(pool).transfer(amount);
        } else {
            IERC20(asset).safeTransfer(pool, amount);
        }
    }

    receive() external payable {
    }

    fallback() external payable {
    }
}

contracts/ingest/IngestRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/**
 * @title Ingest Registry Contract
 * @author Origin Protocol Inc
 */

import "../governance/Governable.sol";

contract IngestRegistry is Governable {
    address public endpointImplementation;
    address public master;
    address public pool;

    event NewEndpointImplementation(
        address indexed newCollector,
        address indexed oldCollector
    );

    event NewMaster(address indexed newMaster, address indexed oldMaster);

    event NewPool(address indexed newPool, address indexed oldPool);

    function setEndpointImplementation(address addr) external onlyGovernor {
        emit NewEndpointImplementation(addr, endpointImplementation);
        endpointImplementation = addr;
    }

    function setMaster(address addr) external onlyGovernor {
        emit NewMaster(addr, master);
        master = addr;
    }

    function setPool(address addr) external onlyGovernor {
        emit NewPool(addr, pool);
        pool = addr;
    }
}

