import "@openzeppelin/contracts/proxy/Proxy.sol";
import "./IngestRegistry.sol";

contract IngestMidProxy is Proxy {
    IngestRegistry constant registry =
        IngestRegistry(0xFFbebEbeBEbeBeBEBeBebeBEbeBebebEAAaAaAAA);

